data.value:
IF NOT EXISTS ( SELECT  1
                FROM    sys.objects
                WHERE   object_id = OBJECT_ID(N'[Log].[#TableName#]')
                        AND type IN ( N'U' ) ) 
    BEGIN 
        SET ANSI_NULLS ON
        SET QUOTED_IDENTIFIER ON
        SET ANSI_PADDING ON
        CREATE TABLE [Log].[#TableName#]
            (
              [LogID] [bigint] IDENTITY(1, 1)
                               NOT NULL,
              [TrigTime] [datetime] NULL,
              [ThreadQueueTime] [varchar](100) NULL,
              [StartExecuteTime] [datetime] NULL,
              [ExecuteTime] [varchar](100) NULL,
              [EndExecuteTime] [datetime] NULL,
              [LogTime] [datetime] NULL,
              [IsCancle] [bit] NULL,
              [IsSuccess] [bit] NULL,
              [RunTaskResult] [text] NULL
            )
        ON  [PRIMARY] TEXTIMAGE_ON [PRIMARY]
        SET ANSI_PADDING OFF
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'日志编号', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'LogID'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'触发时间', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'TrigTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'线程排队耗时', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'ThreadQueueTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'开始执行时间', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'StartExecuteTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'实际执行耗时', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'ExecuteTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'执行结束时间', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'EndExecuteTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'是否取消', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'IsCancle'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'是否成功', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'IsSuccess'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'执行结果', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'RunTaskResult'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = N'日志记录时间', @level0type = N'SCHEMA', @level0name = N'Log',
            @level1type = N'TABLE', @level1name = N'#TableName#',
            @level2type = N'COLUMN', @level2name = N'LogTime'
        EXEC sys.sp_addextendedproperty @name = N'MS_Description',
            @value = @TableDescription, @level0type = N'SCHEMA',
            @level0name = N'Log', @level1type = N'TABLE',
            @level1name = N'#TableName#'
        CREATE CLUSTERED INDEX [Idx_StartEndTime] ON [Log].[#TableName#] ( [StartExecuteTime] ASC, [EndExecuteTime] ASC )
            WITH (
                 PAD_INDEX  = OFF,
                 STATISTICS_NORECOMPUTE  = OFF,
                 SORT_IN_TEMPDB = OFF,
                 IGNORE_DUP_KEY = OFF,
                 DROP_EXISTING = OFF,
                 ONLINE = OFF,
                 ALLOW_ROW_LOCKS  = ON,
                 ALLOW_PAGE_LOCKS  = ON)
        ON  [PRIMARY]
    END
